for(var accordionBtn=document.querySelectorAll(".payment-block__title"),i=0;i<accordionBtn.length;i++)accordionBtn[i].addEventListener("click",function(c){if(c.preventDefault(),this.classList.contains("active-accordion"))this.classList.remove("active-accordion");else{for(var i=0;i<accordionBtn.length;i++)accordionBtn[i].classList.remove("active-accordion");this.classList.add("active-accordion")}});